test_verdure.map:
function(doc) {
  if(doc.timestamp && doc.timestamp > 0){
    doc.voci.forEach(function(voce){
      if(voce.nome === 'Verdure grigliate')
        emit(voce.nome, voce.qta);
    })
  }

}

